function Hotel(e,t,l){this.name=e,this.rooms=t,this.booked=l,this.checkAvailability=function(){return this.rooms-this.booked}}var quayHotel=new Hotel("Quay",60,43),parkHotel=new Hotel("Park Avenue",90,87),details1=quayHotel.name+" rooms: ";details1+=quayHotel.checkAvailability();var elHotel1=document.getElementById("hotel1");elHotel1.textContent=details1;var details2=parkHotel.name+" rooms: ";details2+=parkHotel.checkAvailability();var elHotel2=document.getElementById("hotel2");elHotel2.textContent=details2;