function checkLength(e,n){var t,r;e||(e=window.event),t=e.target||e.srcElement,r=t.nextSibling,t.value.length<n?r.innerHTML="Username must be "+n+"characters or more":r.innerHTML=""}var elUsername=document.getElementById("username");elUsername.addEventListener?elUsername.addEventListener("blur",function(e){checkLength(e,5)},!1):elUsername.attachEvent("onblur",function(e){checkLength(e,5)});