function checkUsername(){var e=el.value;e.length<5?(elMsg.className="warning",elMsg.textContent="Not long enough, yet..."):elMsg.textContent=""}function tipUsername(){elMsg.className="tip",elMsg.innerHTML="Username must be at least 5 characters"}var el=document.getElementById("username"),elMsg=document.getElementById("feedback");el.addEventListener("focus",tipUsername,!1),el.addEventListener("blur",checkUsername,!1);