function success(e){msg="<h3>Longitude:<br />",msg+=e.coords.longitude+"</h3>",msg+="<h3>Latitude:<br />",msg+=e.coords.latitude+"</h3>",elMap.innerHTML=msg}function fail(e){elMap.textContent=e,console.log(e.code)}var elMap=document.getElementById("loc"),msg="Sorry, we were unable to get your location";Modernizr.geolocation?(navigator.geolocation.getCurrentPosition(success,fail),elMap.textContent="Checking location"):elMap.textContent=msg;